mapper.insert:
insert into
  am005_employee (
   employee_id
  ,employee_cd
  ,employee_name1_last
  ,employee_name1_first
  ,employee_name2_last
  ,employee_name2_first
  ,gender_kbn
  ,postcode
  ,prefcode
  ,pref_name1
  ,pref_name2
  ,citycode
  ,city_name1
  ,city_name2
  ,streetaddress1
  ,streetaddress2
  ,birthday
  ,nearest_station_code
  ,nearest_station_name
  ,mail
  ,joined_date
  ,leave_date
  ,employeeattribute_id
  ,biko
  ,insert_user
  ,insert_date
  ,update_user
  ,update_date
  )
values (
   nextval('employee_id_seq')
  ,#{employee_cd}
  ,#{employee_name1_last}
  ,#{employee_name1_first}
  ,#{employee_name2_last}
  ,#{employee_name2_first}
  ,#{gender_kbn}
  ,#{postcode}
  ,#{prefcode}
  ,#{pref_name1}
  ,#{pref_name2}
  ,#{citycode}
  ,#{city_name1}
  ,#{city_name2}
  ,#{streetaddress1}
  ,#{streetaddress2}
  ,#{birthday}
  ,#{nearest_station_code}
  ,#{nearest_station_name}
  ,#{mail}
  ,#{joined_date}
  ,#{leave_date}
  ,#{employeeattribute_id}
  ,#{biko}
  ,#{insert_user}
  ,NOW()
  ,#{insert_user}
  ,NOW()
  )

mapper.select:
select a.employee_id ,a.employee_cd ,a.employee_name1_last ,a.employee_name1_first ,a.employee_name2_last ,a.employee_name2_first ,a.gender_kbn ,a.postcode ,a.prefcode ,a.pref_name1 ,a.pref_name2 ,a.citycode ,a.city_name1 ,a.city_name2 ,a.streetaddress1 ,a.streetaddress2 ,a.birthday ,a.nearest_station_code ,a.nearest_station_name ,a.mail ,a.joined_date ,a.leave_date ,a.employeeattribute_id ,b.organization_cd ,b.start_yearmonth ,a.biko ,a.insert_user ,a.insert_date ,a.update_user ,a.update_date from am005_employee a left join am006_employeebelongs b on b.employee_id = a.employee_id and b.start_yearmonth = (select max(d.start_yearmonth) from am006_employeebelongs d where d.employee_id = b.employee_id
and d.start_yearmonth <= to_char(now(),'YYYYMM'))
a.employee_id = #{employee_id}
and a.employee_cd = #{employee_cd}
and a.mail = #{mail}

mapper.update:
update
  am005_employee
set
   employee_cd          = #{employee_cd}
  ,employee_name1_last  = #{employee_name1_last}
  ,employee_name1_first = #{employee_name1_first}
  ,employee_name2_last  = #{employee_name2_last}
  ,employee_name2_first = #{employee_name2_first}
  ,gender_kbn           = #{gender_kbn}
  ,postcode             = #{postcode}
  ,prefcode             = #{prefcode}
  ,pref_name1           = #{pref_name1}
  ,pref_name2           = #{pref_name2}
  ,citycode             = #{citycode}
  ,city_name1           = #{city_name1}
  ,city_name2           = #{city_name2}
  ,streetaddress1       = #{streetaddress1}
  ,streetaddress2       = #{streetaddress2}
  ,birthday             = #{birthday}
  ,nearest_station_code = #{nearest_station_code}
  ,nearest_station_name = #{nearest_station_name}
  ,mail                 = #{mail}
  ,joined_date          = #{joined_date}
  ,leave_date           = #{leave_date}
  ,employeeattribute_id = #{employeeattribute_id}
  ,biko                 = #{biko}
  ,update_user          = #{update_user}
  ,update_date          = NOW()
where
  employee_id           = #{employee_id}
