mapper.insert:
insert into
  bt003_career (
   employee_id
  ,certification_no
  ,disp_order
  ,start_yearmonth
  ,end_yearmonth
  ,business_content
  ,biko
  ,insert_user
  ,insert_date
  ,update_user
  ,update_date
  )
values (
   #{employee_id}
  ,cast(#{certification_no} as integer)
  ,cast(#{disp_order} as integer)
  ,#{start_yearmonth}
  ,#{end_yearmonth}
  ,#{business_content}
  ,#{biko}
  ,#{insert_user}
  ,NOW()
  ,#{insert_user}
  ,NOW()
  )

mapper.update:
update
  bt003_career
set
   disp_order       = cast(#{disp_order} as integer)
  ,start_yearmonth  = #{start_yearmonth}
  ,end_yearmonth    = #{end_yearmonth}
  ,business_content = #{business_content}
  ,biko             = #{biko}
  ,update_user      = #{update_user}
  ,update_date      = NOW()
where employee_id      = #{employee_id}
  and certification_no = cast(#{certification_no} as integer)

mapper.delete:
delete from bt003_career
where employee_id      = #{employee_id}
  and certification_no = cast(#{certification_no} as integer)
