   #{employee_id}
  ,cast(#{certification_no} as integer)
  ,#{technology_id}
  ,#{biko}
  ,#{insert_user}
  ,NOW()
  ,#{insert_user}
  ,NOW()
  )

mapper.delete:
delete from bt004_careertechnology
where employee_id      = #{employee_id}
  and certification_no = cast(#{certification_no} as integer)
