mapper.insert:
insert into
  bm003_industry (
   industry_id
  ,industry_name
  ,disp_order
  ,biko
  ,insert_user
  ,insert_date
  ,update_user
  ,update_date
  )
values (
   #{industry_id}
  ,#{industry_name}
  ,cast(#{disp_order} as integer)
  ,#{biko}
  ,#{insert_user}
  ,NOW()
  ,#{insert_user}
  ,NOW()
  )

mapper.update:
update
  bm003_industry
set
   industry_name    = #{industry_name}
  ,disp_order       = cast(#{disp_order} as integer)
  ,biko             = #{biko}
  ,update_user      = #{update_user}
  ,update_date      = NOW()
where industry_id   = #{industry_id}
